// Ensure there's at least one input item.
if (!items || items.length === 0) {
  throw new Error("No input items found.");
}

// Our input is expected to have a 'data' property containing the JSONP string.
const input = items[0].json;

if (!input.data) {
  throw new Error("Input JSON does not have a 'data' property.");
}

const rawData = input.data;

// Use a regex to extract the JSON content from the Google Visualization JSONP response.
const regex = /google\.visualization\.Query\.setResponse\((.*)\);?$/s;
const match = rawData.match(regex);

if (!match) {
  throw new Error("Input data does not match the expected Google Visualization JSONP format.");
}

const jsonString = match[1];

// Parse the extracted JSON string.
let parsed;
try {
  parsed = JSON.parse(jsonString);
} catch (error) {
  throw new Error("Failed to parse JSON: " + error.message);
}

// Verify that the parsed JSON has the expected 'table' structure with 'cols' and 'rows'.
if (!parsed.table || !Array.isArray(parsed.table.cols) || !Array.isArray(parsed.table.rows)) {
  throw new Error("Parsed JSON does not have the expected 'table' structure with 'cols' and 'rows'.");
}

const cols = parsed.table.cols;
const rows = parsed.table.rows;

// Helper function to convert date string from "Date(YYYY,M,D)" to "YYYY-MM-DD"
function formatDate(dateStr) {
  const match = dateStr.match(/^Date\((\d+),(\d+),(\d+)\)$/);
  if (!match) return dateStr;
  const year = parseInt(match[1], 10);
  const month = parseInt(match[2], 10) + 1; // JavaScript months are 0-indexed
  const day = parseInt(match[3], 10);
  // Format with leading zeros
  return `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
}

// Map each row into an object using the column labels as keys.
const newItems = rows.map(row => {
  const obj = {};
  cols.forEach((col, index) => {
    let value = row.c && row.c[index] ? row.c[index].v : null;
    // If the column type is "date" and the value is a string that looks like "Date(YYYY,M,D)"
    if (col.type === "date" && typeof value === "string") {
      value = formatDate(value);
    }
    obj[col.label] = value;
  });
  return { json: obj };
});

// Return the new array of items.
return newItems;
